function heroSlider(){return{current:0,init(){setInterval((()=>{this.next()}),6e3)},next(){this.current=(this.current+1)%3},prev(){this.current=0===this.current?2:this.current-1}}}function testimoniSlider(){return{current:0,init(){setInterval((()=>{this.next()}),7e3)},next(){this.current=2===this.current?0:this.current+1},prev(){this.current=0===this.current?2:this.current-1}}}function counter(t,e,n,r){return{targets:{target1:t,target2:e,target3:n,target4:r},count1:0,count2:0,count3:0,count4:0,animated:!1,animate(t,e){let n=null;const r=this,i=s=>{n||(n=s);const a=s-n,c=Math.min(a/2e3,1),u=(o=c)*(2-o)*t;var o;r[e]=u,a<2e3?requestAnimationFrame(i):r[e]=t};requestAnimationFrame(i)},initObserver(t){const e=new IntersectionObserver((n=>{n.forEach((n=>{n.isIntersecting&&!this.animated&&(this.animate(this.targets.target1,"count1"),this.animate(this.targets.target2,"count2"),this.animate(this.targets.target3,"count3"),this.animate(this.targets.target4,"count4"),this.animated=!0,e.unobserve(t))}))}),{threshold:.5});e.observe(t)}}}window.addEventListener("scroll",(()=>{document.getElementById("navbar").classList.toggle("navbar-scrolled",window.scrollY>50)})),document.getElementById("menuBtn").addEventListener("click",(()=>{document.getElementById("mobileMenu").classList.toggle("hidden")}));function sliderData(e,t){return{totalItems:e,itemsPerDesktopPage:t,currentPage:1,get itemsPerCurrentPage(){return window.innerWidth>=768?this.itemsPerDesktopPage:1},get totalPages(){return Math.ceil(this.totalItems/this.itemsPerCurrentPage)},nextPage(){this.currentPage<this.totalPages&&this.currentPage++},prevPage(){this.currentPage>1&&this.currentPage--},init(){window.addEventListener("resize",(()=>{this.currentPage=1}))}}}